import Head from 'next/head'
const Home = () => (
  <div>
    <Head>
      <title>Penggengam Cahaya</title>
      <link rel="icon" href="/favicon.ico" />
      <link rel="stylesheet" href="/font-awesome/css/font-awesome.min.css" />
    </Head>

    <ul>
      <li><a href=""><span><i class="fa fa-facebook fa-lg"></i></span></a></li>
      <li><a href=""><span><i class="fa fa-twitter fa-lg"></i></span></a></li>
      <li><a href=""><span><i class="fa fa-instagram fa-lg"></i></span></a></li>
      <li><a href=""><span><i class="fa fa-whatsapp fa-lg"></i></span></a></li>
      <li className="float-right"><a href=""><span><i class="fa fa-shopping-bag fa-lg"></i></span> Keranjang (0)</a></li>
      <li className="float-right"><a href=""><span><i class="fa fa-user-o fa-lg"></i></span> Login / Daftar</a></li>
      <li className="float-right"><a href=""><span><i class="fa fa-search fa-lg"></i></span></a></li>
    </ul>

    <div className="content">
      <ul class="nav">
        <li><a href="/">Home</a></li>
        <li><a href="/about/">About</a></li>
        <li><a href="/work/">Work</a></li>
        <li><a href="/clients/">Clients</a></li>
        <li><a href="/contact/">Contact</a></li>
      </ul>
    </div>
    <footer>
      <a
        href="https://zeit.co?utm_source=create-next-app&utm_medium=default-template&utm_campaign=create-next-app"
        target="_blank"
        rel="noopener noreferrer"
      >
        Powered by <img src="/zeit.svg" alt="ZEIT Logo" />
      </a>
    </footer>

    <style jsx>{`
      ul {
        list-style-type: none;
        margin: 0;
        padding: 50px 200px 50px 200px;
        overflow: hidden;
        background-color: #fff;
      }

      // li {
      //   float: left;
      //   color: #708189;
      // }

      li a {
        display: block;
        color: black;
        text-align: center;
        padding: 14px 16px;
        text-decoration: none;
      }

      li a:hover:not(.active) {
        color: #0277BD  ;
      }

      .active {
        background-color: #4CAF50;
      }
      .nav{
        border:1px solid #ccc;
        border-width:1px 0;
        list-style:none;
        margin:0;
        padding:0;
        text-align:center;
    }
    .nav li{
        display:inline;
    }
    .nav a{
        display:inline-block;
        padding:10px;
    }

      .container {
        min-height: 100vh;
        padding: 0 0.5rem;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
      }

      .float-right {
        float: right;
      }
      .float-center {
        float: center;
      }
      footer {
        width: 100%;
        height: 100px;
        border-top: 1px solid #eaeaea;
        background-color: #F5F6F8;  
        display: flex;
        justify-content: center;
        align-items: center;
      }

      .content {
        margin: 0;
        padding: 0px 200px 0px 200px;
      }

      hr {
        background-color: #E2E2E2;
      }

      footer img {
        margin-left: 0.5rem;
      }

      footer a {
        display: flex;
        justify-content: center;
        align-items: center;
      }

      a {
        color: inherit;
        text-decoration: none;
      }
    `}</style>

    <style jsx global>{`
      html,
      body {
        padding: 0;
        margin: 0;
        font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen,
          Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif;
      }

      * {
        box-sizing: border-box;
      }
    `}</style>
  </div>
)

export default Home
